import { useRef, useState } from 'react/cjs/react.production.min';
import classes from '../UI/MealForm.module.css';
import FormInput from './FormInput';
const MealForm = (props)=>{
   const amountRef = useRef();
   const [isValid, setisValid]= useState(true);
   const submitHandler = ()=>{
    const amount = amountRef.current.value;
    const amountNum = +amount;
    if(amountNum === 0 || amountNum <1 || amountNum > 5){
      setisValid(true);
    }
    props.onAddtoCart(amountNum);
   }
    return (
      <form className={classes.form} on>
        <FormInput label="Amount" input={{
            ref: {amountRef},
            id: 'amount_'+props.id,
            type:'number',
            min:'1',
            max:'5',
            step:'1',
            defaultValue:'1'
        }}/>
        <button>+ Add</button>
        {!isValid && <p>Please enter a valid number from 1 to 5.</p>}
      </form>
    );
}

export default MealForm;
