import { useContext } from 'react';
import classes from '../UI/Cart.module.css';
import Modal from '../UI/Modal';
import CartContext from '../store/cart-context';
import CartItem from '../Cart/CartItem';
const Cart = (props) =>{
    const cartItemsCtx = useContext(CartContext);
    const removeItemHanlder = (id)={};
     const Hanlder = (id = {});
    const cartItems = (
      <ul className={classes["cart-items"]}>
        {cartItemsCtx.items.map((item) => (
          <CartItem key={item.id} id={item.id} name={item.name} price={item.price} amount={item.amount} onRemove = {removeItemHanlder} onAdd = {addItemHandler}/>
        ))}
      </ul>
    );
    
    return (
      <Modal onClick={props.onClose}>
        {cartItems}
        <div className={classes.total}>
          <span>Total Amount</span>
          <span>123</span>
        </div>
        <div className={classes.actions}>
          <button className={classes["button--alt"]} onClick={props.onClose}>
            Close
          </button>
          <button className={classes.button}>Order</button>
        </div>
      </Modal>
    );
}

export default Cart;
