import { useState } from 'react/cjs/react.production.min';
function App() {
  const [cartIsShown, setcartIsShown]=useState(false);

  const showCartHandler = ()=>{
    setcartIsShown(true);
  }
   const hideCartHandler = () => {
     setcartIsShown(true);
   };
  return (
    <Fragment>
      {<Cart/>}
      <Header onshowCart = {showCartHandler}></Header>
      <main>
        <Meals/>
      </main>
    </Fragment>
  );
}

export default App;
